class Time {

    int hour;
    int minute;
    int second;

    public static Time noon() {
        // write your code here
    }

    public static Time midnight() {
        // write your code here
    }

    public static Time ofSeconds(long seconds) {
        // write your code here
    }

    public static Time of(int hour, int minute, int second) {
        // write your code here
    }
